SELECT e.first_name, e.last_name, e.hire_date, s.salary FROM employees e JOIN dept_emp de ON e.emp_no = de.emp_no JOIN departments d ON de.dept_no = d.dept_no JOIN salaries s ON e.emp_no = s.emp_no WHERE d.dept_name = 'Sales' AND e.hire_date BETWEEN '1985-01-01' AND '1989-12-31' AND de.to_date = '9999-01-01' AND s.to_date = '9999-01-01' ORDER BY s.salary DESC;
